        return new Source("{% extends '@Publication/Back/Back.html.twig' %}
{% block titre %}{{ parent() }} - Acceuil {% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <!-- DataTables -->
    <link rel=\"stylesheet\" href=\"{{ asset('plugins/datatables-bs4/css/dataTables.bootstrap4.css') }}\">
{% endblock %}

{% block content %}
    <!-- Content Wrapper. Contains page content -->
    <div class=\"content-wrapper\">

        <!-- Main content -->
        <section class=\"content\">
            <div class=\"row\">
                <div class=\"col-12\">

                    <div class=\"card\">
                        <div class=\"card-header\">
                            <h3 class=\"card-title\">Publication</h3>
                        </div>
                        <!-- /.card-header -->
                        <div class=\"card-body\">
                            <table id=\"tableau\" class=\"table table-bordered table-striped\">
                                <thead>
                                <tr>
                                    <th>Titre</th>
                                    <th>Description</th>
                                    <th>Image</th>




                                    <th>Delete</th>


                                </tr>
                                </thead>
                                <tbody>
                                {% for cc in c %}
                                    <tr>
                                        <td>{{ cc.titre }}</td>
                                        <td>{{ cc.description }}</td>
                                        <td><div  class=\"blog-post-thumbnail-inner\"><img id=\"{{ cc.image }}\">

                                                <script>
                                                    var imageurl = \"{{ cc.image }}\";
                                                    var index = -1;

                                                    for(var i = 0 ; i< imageurl.length ; i++)
                                                    {if(imageurl[i]==\"/\"){index = i;}}
                                                    if(index != -1)imageurl=\"/front/images\"+imageurl.substr(index,imageurl.length);
                                                    else imageurl = \"/front/images\"+imageurl;

                                                    document.getElementById(\"{{ cc.image }}\").src = imageurl;
                                                </script>   </div></td>



                                        <td>
                                            <a href=\"{{ path('Deleteback',{'id':cc.id}) }}\" class=\"btn btn-block btn-danger\">Supprimer</a>
                                        </td>
                                        <td>

                                        </td>
                                    </tr>
                                {% endfor %}
                                </tfoot>
                            </table>
                        </div>
                        <!-- /.card-body -->
                    </div>
                    <!-- /.card -->
                </div>
                <!-- /.col -->
            </div>
            <!-- /.row -->
        </section>

    </div>
{% endblock %}

{% block javascripts %}
    {{ parent() }}
    <!-- DataTables -->
    <script src=\"{{ asset('plugins/datatables/jquery.dataTables.js') }}\"></script>
    <script src=\"{{ asset('plugins/datatables-bs4/js/dataTables.bootstrap4.js') }}\"></script>
